import React from 'react';
import './AboutSection.css';

function AboutSection() {
  return (
    <div className="about-section">
      <div className="about-description">
        <h1>About Arbab Pack Ltd</h1>
        <p>
          Arbab Pack Ltd is one of the leading Paper Converting & Flexible Packaging material manufacturers in Bangladesh. We have started our journey in 1988; from then Arbab has succeeded in developing and emerging as one of the leading manufacturers. We believe we are always ready & confident to take any challenges anytime.
        </p>
        <p>
          Our aim is to be on the leading edge of technological development and provide creative, competitive, and well-informed solutions that help our clients reduce costs. We are closely following market trends and preparing ourselves to face the challenges of the future.
        </p>
      </div>
    </div>
  );
}

export default AboutSection;
